// In your main dashboard/page component
import React, { useState } from "react";
import PlaceOrderForm from "./PlaceOrderForm"; // The component above

function DashboardPage() {
  const [showOrderForm, setShowOrderForm] = useState(false);

  // Example: Inside your nav or menu
  const handlePlaceOrder = () => setShowOrderForm(true);

  return (
    <>
      {/* navbar here, pass handlePlaceOrder to "Place Order" button */}
      <PlaceOrderForm open={showOrderForm} onClose={() => setShowOrderForm(false)} />
      {/* rest of your page */}
    </>
  );
}
