import React, { useState } from 'react';
import {
  Box, Card, CardContent, Typography, TextField, Button, Divider
} from '@mui/material';
import { Google as GoogleIcon } from '@mui/icons-material';
import { GoogleLogin } from 'react-google-login';

function Login() {
  const [showOTP, setShowOTP] = useState(false);
  const [phone, setPhone] = useState('');
  const [otp, setOTP] = useState('');

  const requestOTP = () => {
    // TODO: connect to backend/Firebase to send OTP
    setShowOTP(true);
  };

  const handleGoogleSuccess = (response) => {
    // TODO: connect to backend with Google Auth result
    alert('Google Auth Success!');
  };

  return (
    <Box sx={{
      minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', bgcolor: '#fff'
    }}>
      <Card sx={{
        minWidth: 350, maxWidth: 380, borderRadius: 4, boxShadow: 6
      }}>
        <CardContent>
          <Typography variant="h5" align="center" sx={{ mt: 2, color: '#e23744', fontWeight: 700 }}>
            Login
          </Typography>
          <Box sx={{ mt: 3 }}>
            {!showOTP ? (
              <>
                <TextField
                  label="Phone Number"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                />
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }} onClick={requestOTP}>Request OTP</Button>
              </>
            ) : (
              <>
                <TextField
                  label="Enter OTP"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={otp}
                  onChange={(e) => setOTP(e.target.value)}
                />
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }}>Login</Button>
              </>
            )}
          </Box>
          <Divider sx={{ my: 3 }}>OR</Divider>
          <GoogleLogin
            clientId="YOUR_GOOGLE_CLIENT_ID"
            onSuccess={handleGoogleSuccess}
            onFailure={() => alert('Google Auth Failed')}
            cookiePolicy={'single_host_origin'}
            render={renderProps => (
              <Button
                fullWidth
                startIcon={<GoogleIcon />}
                sx={{
                  border: '1px solid #dadada', borderRadius: 99,
                  py: 1.2, fontWeight: 700, color: '#222', bgcolor: '#fff'
                }}
                onClick={renderProps.onClick}
                disabled={renderProps.disabled}
              >
                Login with Google
              </Button>
            )}
          />
          <Typography variant="body2" align="center" sx={{ mt: 2 }}>
            Don&apos;t have an account? <a href="/signup" style={{ color: '#e23744', textDecoration: 'none' }}>Sign Up</a>
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
}

export default Login;
