import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Stack } from "@mui/material";

export default function OrderHistory() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Try to fetch from backend API (replace /api/orders with your endpoint)
    fetch("/api/orders")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setOrders(data);
        else setOrders([]);
      })
      .catch(() => {
        // If API fails or for local dev, fallback to localStorage for user
        let data = [];
        try {
          data = JSON.parse(localStorage.getItem("orders") || "[]");
          if (!Array.isArray(data)) data = [];
        } catch {
          data = [];
        }
        setOrders(data);
      });
  }, []);

  return (
    <Box minHeight="100vh" bgcolor="#fafafc" py={6} px={2}>
      <Typography variant="h4" fontWeight={800} mb={4}>
        My Orders
      </Typography>
      {orders.length === 0 ? (
        <Typography>No orders found.</Typography>
      ) : (
        <Stack spacing={2}>
          {orders.map(order => (
            <Paper key={order.id || order.time} sx={{ p: 3, borderRadius: 3 }}>
              <Typography>
                <b>Order ID:</b> {order.id}
              </Typography>
              <Typography>
                <b>Payment:</b> {order.paymentMethod}
              </Typography>
              <Typography>
                <b>Status:</b> {order.status}
              </Typography>
              <Typography>
                <b>Time:</b> {order.time}
              </Typography>
            </Paper>
          ))}
        </Stack>
      )}
    </Box>
  );
}
