import React from "react";

// This component acts as a modal dialog/order form.
function OrderForm({ open, onClose }) {
  if (!open) return null;

  return (
    <div style={{
      position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
      background: "rgba(0,0,0,0.4)", display: "flex", justifyContent: "center", alignItems: "center"
    }}>
      <div style={{
        background: "#fff", padding: 32, borderRadius: 8, minWidth: 300, minHeight: 100
      }}>
        <h2>Place Order</h2>
        {/* Add your form fields here */}
        <form>
          <div>
            <label>Fuel Type:</label>
            <input type="text" placeholder="Petrol/Diesel" />
          </div>
          <div>
            <label>Quantity (litres):</label>
            <input type="number" placeholder="e.g. 10" />
          </div>
          <div>
            <label>Delivery Address:</label>
            <input type="text" placeholder="Enter your address" />
          </div>
          <button type="submit">Submit Order</button>
        </form>
        <button onClick={onClose} style={{ marginTop: 12 }}>Close</button>
      </div>
    </div>
  );
}

export default OrderForm;
