import React from "react";
import { BrowserRouter, Routes, Route, Link, useLocation } from "react-router-dom";
import { AppBar, Toolbar, Button, Container } from "@mui/material";
import { GoogleOAuthProvider } from "@react-oauth/google";

import HomePage from "./pages/HomePage";
import OrderPage from "./pages/OrderPage";
import LoginPage from "./pages/LoginPage";
import SignupPage from "./pages/SignupPage";
import MerchantSignup from "./pages/MerchantSignup";
import AggregatorSignup from "./pages/AggregatorSignup";
import UserDashboard from "./pages/UserDashboard";

// This helper will inject CSS only when on /dashboard
function DashboardCSSWrapper({ children }) {
  const location = useLocation();
  React.useEffect(() => {
    // If on /dashboard route, inject our dashboard-only CSS
    if (location.pathname === "/dashboard") {
      const styleElem = document.createElement("style");
      styleElem.innerHTML = `
        html, body, #root {
          height: 100%;
          width: 100%;
          margin: 0;
          padding: 0;
          overflow: hidden !important;
          background: #fff;
        }
      `;
      document.head.appendChild(styleElem);
      return () => { document.head.removeChild(styleElem); };
    }
  }, [location]);
  return children;
}

function App() {
  return (
    <GoogleOAuthProvider clientId="565209562551-8kl75442g9j39u158hpa7j8ns3rrkbma.apps.googleusercontent.com">
      <BrowserRouter>
        <DashboardCSSWrapper>
          <AppBar
            position="static"
            color="default"
            elevation={1}
            sx={{ mb: 0, bgcolor: "#fff" }}
          >
            <Toolbar>
              <Button component={Link} to="/" color="inherit">
                Home
              </Button>
              {/* All other buttons removed as per your request */}
            </Toolbar>
          </AppBar>
          <Container maxWidth="md" sx={{ mt: 4 }}>
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/order" element={<OrderPage />} />
              <Route path="/login" element={<LoginPage />} />
              {/* Customer sign up */}
              <Route path="/signup" element={<SignupPage />} />
              {/* Merchant sign up */}
              <Route path="/merchant-signup" element={<MerchantSignup />} />
              {/* Aggregator sign up */}
              <Route path="/aggregator-signup" element={<AggregatorSignup />} />
              {/* User Dashboard */}
              <Route path="/dashboard" element={<UserDashboard />} />
            </Routes>
          </Container>
        </DashboardCSSWrapper>
      </BrowserRouter>
    </GoogleOAuthProvider>
  );
}

export default App;
