import React from "react";
import {
  AppBar, Toolbar, Typography, IconButton, Menu, MenuItem, Avatar,
  Box, Card, Divider, Button, Badge
} from "@mui/material";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import WalletIcon from "@mui/icons-material/AccountBalanceWallet";
import StarIcon from "@mui/icons-material/Star";
import MenuIcon from "@mui/icons-material/Menu";

const navLinks = [
  { label: "Home" },
  { label: "Masters", menu: ["Master 1", "Master 2"] },
  { label: "Order", menu: ["Place an Order", "My Cart", "My Orders"] },
  { label: "Reports", menu: ["Report 1", "Report 2"] },
  { label: "Integration Services" },
  { label: "Customer Services" },
  { label: "Learning" }
];

export default function FuelNowDashboard() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [menuIdx, setMenuIdx] = React.useState(null);
  const handleMenuOpen = (e, idx) => { setAnchorEl(e.currentTarget); setMenuIdx(idx); };
  const handleMenuClose = () => { setAnchorEl(null); setMenuIdx(null); };

  return (
    <Box
      sx={{
        minHeight: "100vh",
        width: "100vw",
        margin: 0,
        padding: 0,
        boxSizing: "border-box",
        fontFamily: "Inter, Arial, sans-serif",
        display: "flex",
        flexDirection: "column"
      }}
    >
      {/* NAVBAR */}
      <AppBar position="static" sx={{ bgcolor: "#e23744", boxShadow: 0 }}>
        <Toolbar sx={{
          minHeight: 60,
          px: { xs: "2vw", md: "3vw" },
          justifyContent: "space-between"
        }}>
          {/* Branding */}
          <Typography variant="h6" fontWeight={900} sx={{
            mr: 3, fontSize: 32, letterSpacing: 2, color: "#fff"
          }}>FUELnow</Typography>
          {/* Nav */}
          <Box sx={{ display: { xs: "none", md: "flex" }, gap: 3, flexGrow: 1 }}>
            {navLinks.map((item, idx) => (
              <Box key={item.label}>
                {item.menu ? (
                  <>
                    <Button
                      sx={{ color: "#fff", fontWeight: 700, fontSize: 15 }}
                      onClick={e => handleMenuOpen(e, idx)}
                    >{item.label}</Button>
                    <Menu
                      anchorEl={anchorEl}
                      open={menuIdx === idx}
                      onClose={handleMenuClose}
                    >
                      {item.menu.map(sub => (
                        <MenuItem key={sub} onClick={handleMenuClose}>{sub}</MenuItem>
                      ))}
                    </Menu>
                  </>
                ) : (
                  <Button
                    sx={{ color: "#fff", fontWeight: 700, fontSize: 15 }}
                  >{item.label}</Button>
                )}
              </Box>
            ))}
          </Box>
          {/* User section */}
          <Box sx={{ display: "flex", alignItems: "center" }}>
            <IconButton size="large" sx={{ color: "#fff" }}>
              <Badge badgeContent={0} color="error">
                <ShoppingCartIcon sx={{ fontSize: 27 }} />
              </Badge>
            </IconButton>
            <Typography sx={{ color: "#fff", fontWeight: 700, fontSize: 17, mr: 2 }}>
              Hello, User
            </Typography>
            <Avatar sx={{
              width: 39, height: 39,
              bgcolor: "#fff", color: "#e23744",
              fontWeight: 900, fontSize: 18
            }}>U</Avatar>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Main Content - use flex-grow to fill remaining space */}
      <Box sx={{
        bgcolor: "#fff5f7",
        flexGrow: 1,
        display: "flex",
        flexDirection: "column",
        justifyContent: "flex-start",
        alignItems: "stretch",
        width: "100vw",
        pt: 2,
        overflow: "hidden"
      }}>
        {/* Metrics row: full width, cards not centered/marginless, row & wrap */}
        <Box sx={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "flex-start",
          alignItems: "stretch",
          gap: 3,
          px: 2,
          pb: 2,
          width: "100vw",
          boxSizing: "border-box"
        }}>
          <Card sx={{
            p: 3, minWidth: 330, flex: 1, borderRadius: 2, boxShadow: 3,
            bgcolor: "#fff", display: "flex", flexDirection: "column"
          }}>
            <Box display="flex" alignItems="center" mb={1}>
              <StarIcon sx={{ color: "#e23744", fontSize: 32, mr: 1.1 }} />
              <Typography fontWeight={700} sx={{ color: "#e23744", fontSize: 17 }}>
                Available Loyalty
              </Typography>
            </Box>
            <Typography variant="h5" fontWeight={900} sx={{ ml: 5 }}>₹0</Typography>
            <Typography variant="body2" sx={{ opacity: .7, ml: 5, mt: 1 }}>
              Blocked (In Cart, Payment Initiated)
            </Typography>
          </Card>
          <Card sx={{
            p: 3, minWidth: 330, flex: 1, borderRadius: 2, boxShadow: 3,
            bgcolor: "#fff", display: "flex", flexDirection: "column"
          }}>
            <Box display="flex" alignItems="center" mb={1}>
              <WalletIcon sx={{ color: "#e23744", fontSize: 32, mr: 1.1 }} />
              <Typography fontWeight={700} sx={{ color: "#e23744", fontSize: 17 }}>
                My Wallet Limit
              </Typography>
            </Box>
            <Typography variant="h5" fontWeight={900} sx={{ ml: 5 }}>₹0</Typography>
            <Button size="small" variant="contained"
              sx={{
                bgcolor: "#e23744", color: "#fff",
                borderRadius: 99, px: 2.5, py: 0.2, fontWeight: 700, fontSize: 13,
                mt: 1, width: "fit-content", ml: 5
              }}>
              STATUS ACTIVE
            </Button>
          </Card>
          <Card sx={{
            p: 3, minWidth: 330, flex: 1, borderRadius: 2, boxShadow: 3,
            bgcolor: "#fff", display: "flex", flexDirection: "column"
          }}>
            <Box display="flex" alignItems="center" mb={1.2}>
              <ShoppingCartIcon sx={{ color: "#e23744", fontSize: 32, mr: 1.1 }} />
              <Typography fontWeight={700} sx={{ color: "#e23744", fontSize: 17 }}>
                Cart Status
              </Typography>
            </Box>
            <Typography variant="h5" fontWeight={900} sx={{ ml: 5 }}>Active</Typography>
          </Card>
        </Box>

        {/* Details row */}
        <Box sx={{
          display: "flex",
          flexDirection: "row",
          gap: 3,
          px: 2,
          pb: 2,
          width: "100vw",
          boxSizing: "border-box"
        }}>
          <Card sx={{
            flex: 2, minWidth: 0, borderRadius: 2, boxShadow: 3, bgcolor: "#fff", p: 0, overflow: "visible"
          }}>
            <Box display="flex" alignItems="center" justifyContent="space-between" px={3} pt={2.1}>
              <Typography variant="h6" fontWeight={900} sx={{ color: "#e23744" }}>
                My Last 5 Orders
              </Typography>
              <Button variant="text"
                sx={{
                  background: "#ffe4ec", color: "#e23744",
                  borderRadius: 99, fontWeight: 700,
                  textTransform: "none", px: 3, py: .7,
                }}>
                More
              </Button>
            </Box>
            <Divider sx={{ mt: 2, mb: 2 }} />
            <Typography sx={{ opacity: 0.7, px: 3 }}>No records found</Typography>
            <Box sx={{ height: 32 }} /> {/* force no collapse */}
          </Card>
          <Card sx={{
            flex: 1, minWidth: 0, borderRadius: 2, boxShadow: 3, bgcolor: "#fff", p: 0, overflow: "visible"
          }}>
            <Box display="flex" alignItems="center" justifyContent="space-between" px={3} pt={2.1}>
              <Typography variant="h6" fontWeight={900} sx={{ color: "#e23744" }}>
                My Assets
              </Typography>
              <Button variant="text"
                sx={{
                  background: "#ffe4ec", color: "#e23744",
                  borderRadius: 99, fontWeight: 700,
                  textTransform: "none", px: 3, py: .7,
                }}>
                More
              </Button>
            </Box>
            <Divider sx={{ mt: 2, mb: 2 }} />
            <Typography sx={{ opacity: 0.7, px: 3 }}>No records found</Typography>
            <Box sx={{ height: 32 }} /> {/* force no collapse */}
          </Card>
        </Box>
      </Box>

      {/* Sticky FOOTER */}
      <Box sx={{
        bgcolor: "#fff",
        color: "#e23744",
        py: 2,
        textAlign: "center",
        fontWeight: 600,
        letterSpacing: ".3px",
        fontSize: 15
      }}>
        &copy; 2025 FUELnow. All rights reserved. | Contact | Privacy Policy
      </Box>
    </Box>
  );
