import React from "react";
import {
  AppBar, Toolbar, Typography, Box, Avatar, IconButton, Menu, MenuItem, Card, Divider, Button, List, ListItem, ListItemIcon, ListItemText, Drawer, Badge
} from "@mui/material";
import PersonIcon from "@mui/icons-material/Person";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import EditIcon from "@mui/icons-material/Edit";
import PhoneIphoneIcon from "@mui/icons-material/PhoneIphone";
import EmailIcon from "@mui/icons-material/Email";
import VpnKeyIcon from "@mui/icons-material/VpnKey";
import LogoutIcon from "@mui/icons-material/Logout";

// Dropdown menu data
const navDropdowns = [
  {label: "Masters", items: ["Vehicle Master", "Product Master"]},
  {label: "Order", items: ["Place an Order", "My Cart", "My Orders"]},
  {label: "Reports", items: ["Order History", "Wallet Reports"]},
];
const navDirect = [
  {label: "Home", link: "#"},
  {label: "Customer Service", link: "#"},
];

export default function ZomatoDashboard() {
  const [profileOpen, setProfileOpen] = React.useState(false);
  const [navAnchor, setNavAnchor] = React.useState(null);
  const [dropdownIdx, setDropdownIdx] = React.useState(-1);

  // handle nav dropdown open/close
  const handleDropdownOpen = (idx, event) => {
    setDropdownIdx(idx);
    setNavAnchor(event.currentTarget);
  };
  const handleDropdownClose = () => {
    setDropdownIdx(-1);
    setNavAnchor(null);
  };

  return (
    <Box>
      {/* NAVIGATION BAR */}
      <AppBar position="static" className="app-bar-zmt" sx={{width: "100vw"}}>
        <Toolbar className="zmt-navbar-content" sx={{minHeight: "60px!important"}}>
          {/* Brand/Logo */}
          <Box sx={{display: "flex", alignItems: "center", mr: 2}}>
            <Avatar sx={{width: 38, height: 38, bgcolor: "#fff", color: "#e23744", fontWeight: 900}}>
              <PersonIcon fontSize="medium" />
            </Avatar>
            <Typography variant="h5" fontWeight={900} ml={1.1} sx={{letterSpacing: 1.6}}>
              FUELnow
            </Typography>
          </Box>
          {/* Center Nav */}
          <Box sx={{display:"flex",alignItems:"center"}}>
            {/* Direct links */}
            {navDirect.map(el => (
              <Button className="zmt-nav-link" sx={{mr: 1.5}} key={el.label}>
                {el.label}
              </Button>
            ))}
            {/* Dropdown navs */}
            {navDropdowns.map((el, idx) => (
              <Box key={el.label} sx={{position:"relative"}}>
                <Button
                  className="zmt-nav-link"
                  aria-controls={`nav-dropdown-${idx}`}
                  aria-haspopup="true"
                  onMouseEnter={e => handleDropdownOpen(idx,e)}
                  onMouseLeave={handleDropdownClose}
                  sx={{color:"#fff", mr: 1.3, minWidth:110}}
                >
                  {el.label}
                  <span style={{marginLeft:4,fontWeight:400,fontSize:13}}>▼</span>
                </Button>
                <Menu
                  id={`nav-dropdown-${idx}`}
                  anchorEl={navAnchor}
                  open={dropdownIdx === idx}
                  onClose={handleDropdownClose}
                  MenuListProps={{
                    onMouseLeave: handleDropdownClose,
                    sx: {p:0,mt:"3px"},
                  }}
                  className="zmt-navbar-dropdown-menu"
                >
                  {el.items.map(i =>
                    <MenuItem key={i} sx={{fontWeight:600, fontSize:15}} onClick={handleDropdownClose}>{i}</MenuItem>
                  )}
                </Menu>
              </Box>
            ))}
          </Box>
          {/* Right: Profile/avatar */}
          <Box sx={{display:"flex",alignItems:"center", ml:2, mr:2}}>
            <IconButton size="large" sx={{ color: "#fff" }}>
              <Badge badgeContent={0} color="error">
                <ShoppingCartIcon sx={{ fontSize: 22 }} />
              </Badge>
            </IconButton>
            <IconButton onClick={()=>setProfileOpen(true)} sx={{p:0,ml:1}}>
              <Avatar sx={{
                width: 33, height: 33,
                bgcolor: "#ffd1d1", color: "#b71c1c",
                fontWeight: 700, fontSize: 17
              }}>
                J
              </Avatar>
            </IconButton>
          </Box>
        </Toolbar>
      </AppBar>
      {/* PROFILE DRAWER */}
      <Drawer
        anchor="right"
        open={profileOpen}
        onClose={()=>setProfileOpen(false)}
        sx={{
          "& .MuiDrawer-paper": {
            width: 305,
            borderTopLeftRadius: 28,
            borderBottomLeftRadius: 28,
            boxShadow: 6
          }
        }}>
        <Box sx={{ p: 3, pt: 4, display: "flex", flexDirection: "column", alignItems: "center" }}>
          <IconButton sx={{ position: "absolute", right: 15, top: 8 }} onClick={()=>setProfileOpen(false)}>
            <Typography variant="h5" sx={{ fontWeight: 700, color: "#222" }}>×</Typography>
          </IconButton>
          <Avatar sx={{ width: 76, height: 76, bgcolor: "#ffd1d1", color: "#b71c1c", fontWeight: 900, fontSize: 34, mb: 1.6, boxShadow: 2 }}>
            J
          </Avatar>
          <Typography fontWeight={900} sx={{ fontSize: 20.5, color: "#e23744",mb:0.8 }}>
            jai devarajan
          </Typography>
          <Button startIcon={<EditIcon />} variant="contained"
            sx={{
              borderRadius: 99, bgcolor: "#e23744", color: "#fff",
              fontWeight: 700, px: 3.8, boxShadow: 2, fontSize: 15, mb: 2,
              textTransform: "none", '&:hover': { bgcolor: "#b71c1c" }
            }}
          >Edit Profile</Button>
        </Box>
        <Divider />
        <List>
          <ListItem button>
            <ListItemIcon><PhoneIphoneIcon sx={{color: "#e23744"}} /></ListItemIcon>
            <ListItemText primary="Update Mobile" />
          </ListItem>
          <ListItem button>
            <ListItemIcon><EmailIcon sx={{color: "#e23744"}} /></ListItemIcon>
            <ListItemText primary="Update Email" />
          </ListItem>
          <ListItem button>
            <ListItemIcon><VpnKeyIcon sx={{color: "#e23744"}} /></ListItemIcon>
            <ListItemText primary="Update Password" />
          </ListItem>
          <ListItem button>
            <ListItemIcon><LogoutIcon sx={{color: "#e23744"}} /></ListItemIcon>
            <ListItemText primary="Log Out" />
          </ListItem>
        </List>
      </Drawer>

      {/* DASHBOARD BODY */}
      <Box className="dashboard-body-container">
        <Box className="dashboard-card-panel-zmt">
          {/* CARD 1 */}
          <Card className="zmt-dashboard-card zmt-red-card">
            <Box p={2.1}>
              <Typography className="zmt-dashboard-metrics-title">AVAILABLE LOYALTY</Typography>
              <Typography className="zmt-dashboard-currency">₹0</Typography>
              <Typography className="zmt-dashboard-metrics-desc">
                Available Loyalty - Blocked(In Cart, Payment Initiated)
              </Typography>
            </Box>
          </Card>
          {/* CARD 2 */}
          <Card className="zmt-dashboard-card zmt-yellow-card">
            <Box p={2.1}>
              <Typography className="zmt-dashboard-metrics-title" sx={{color:"#b67e00"}}>MY WALLET LIMIT</Typography>
              <Typography className="zmt-dashboard-currency">₹0</Typography>
              <Typography className="zmt-dashboard-metrics-desc">
                Amount available in your wallet
              </Typography>
            </Box>
          </Card>
          {/* CARD 3 */}
          <Card className="zmt-dashboard-card zmt-white-card">
            <Box p={2.1}>
              <Typography className="zmt-dashboard-metrics-title" sx={{color:"#e23744"}}>My Last 5 Orders</Typography>
              <Divider sx={{my: 1,opacity:.7}}/>
              <Typography fontSize={15} color="#222" sx={{opacity:0.8}}>No records found</Typography>
            </Box>
          </Card>
        </Box>
        <Card className="zmt-dashboard-assets-card">
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={.8}>
            <Typography className="zmt-asset-title">My Assets</Typography>
          </Box>
          <Divider className="zmt-assets-divider"/>
          <Typography color="#444" fontSize={15}>No records found</Typography>
        </Card>
      </Box>
    </Box>
  );
}
