import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import PhoneIphoneIcon from "@mui/icons-material/PhoneIphone";
import EmailIcon from "@mui/icons-material/Email";
import VpnKeyIcon from "@mui/icons-material/VpnKey";
import LogoutIcon from "@mui/icons-material/Logout";
import EditIcon from "@mui/icons-material/Edit";

const navLinks = [
  "Home",
  "Masters",
  "Order",
  "Reports",
  "Integration Services",
  "Customer Services",
  "Learning",
];

const drawerWidth = 310;

export default function FuelNowDashboard() {
  const [profileOpen, setProfileOpen] = React.useState(false);

  // Disable ALL page scrolling when profile drawer is open
  React.useEffect(() => {
    if (profileOpen) {
      document.body.style.overflow = "hidden";
      document.documentElement.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
      document.documentElement.style.overflow = "";
    }
    return () => {
      document.body.style.overflow = "";
      document.documentElement.style.overflow = "";
    };
  }, [profileOpen]);

  return (
    <Box
      sx={{
        minHeight: "100vh",
        bgcolor: "#fff5f7",
        width: "100vw",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        alignItems: "stretch",
      }}
    >
      {/* NAVBAR */}
      <AppBar
        position="static"
        sx={{
          bgcolor: "#e23744",
          boxShadow: 0,
          width: "100vw",
        }}>
        <Toolbar sx={{
          minHeight: 60,
          justifyContent: "center",
          width: "100vw",
          p: 0,
        }}>
          <Box
            sx={{
              width: "100vw",
              maxWidth: "100vw",
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              px: 4,
            }}
          >
            {/* Branding */}
            <Typography variant="h5" fontWeight={900} sx={{
              mr: 3, fontSize: 32, letterSpacing: 2, color: "#fff"
            }}>FUELnow</Typography>

            {/* Nav links */}
            <Box sx={{
              display: "flex", gap: 2,
              flex: 1,
              justifyContent: "center"
            }}>
              {navLinks.map(label => (
                <Button
                  key={label}
                  sx={{
                    color: "#fff",
                    fontWeight: 700,
                    fontSize: 15,
                    borderRadius: 2,
                    textTransform: "none",
                    px: 1.5,
                  }}
                >{label}</Button>
              ))}
            </Box>

            {/* Right side: cart & profile */}
            <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
              <IconButton size="large" sx={{ color: "#fff" }}>
                <Badge badgeContent={0} color="error">
                  <ShoppingCartIcon sx={{ fontSize: 27 }} />
                </Badge>
              </IconButton>
              <Typography
                sx={{
                  color: "#fff",
                  fontWeight: 700,
                  fontSize: 17,
                  mr: 1,
                  display: { xs: "none", sm: "inline-block" },
                }}
              >Hello, User</Typography>
              <Avatar
                sx={{
                  width: 39, height: 39,
                  bgcolor: "#fff", color: "#e23744",
                  fontWeight: 900, fontSize: 18,
                  cursor: "pointer",
                }}
                onClick={() => setProfileOpen(true)}
              >U</Avatar>
            </Box>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Profile Drawer */}
      <Drawer
        anchor="right"
        open={profileOpen}
        onClose={() => setProfileOpen(false)}
        sx={{
          "& .MuiDrawer-paper": {
            width: drawerWidth,
            borderTopLeftRadius: 22,
            borderBottomLeftRadius: 22,
            boxShadow: 5,
            bgcolor: "#fff"
          }
        }}
      >
        <Box sx={{ p: 3, display: "flex", flexDirection: "column", alignItems: "center" }}>
          <IconButton
            sx={{ position: "absolute", right: 12, top: 12 }}
            onClick={() => setProfileOpen(false)}
            aria-label="Close"
          >
            <Typography variant="h5" sx={{ fontWeight: 700, color: "#e23744" }}>×</Typography>
          </IconButton>
          <Avatar sx={{
            width: 87, height: 87, bgcolor: "#fff5f7", color: "#e23744",
            fontWeight: 900, fontSize: 44, mb: 1, boxShadow: 2
          }}>
            U
          </Avatar>
          <Typography fontWeight={700} sx={{ fontSize: 22, color: "#222", mt: 1 }}>
            jai devarajan
          </Typography>
          <Button
            startIcon={<EditIcon />}
            variant="outlined"
            sx={{
              mt: 1, borderRadius: 99, borderColor: "#e23744", color: "#e23744",
              fontWeight: 600,
              px: 3, py: 0.3,
              textTransform: "none",
              fontSize: 15,
              '&:hover': { bgcolor: "#fff3f4", borderColor: "#e23744" }
            }}
          >Edit Profile</Button>
        </Box>
        <Divider />
        <List>
          <ListItem button>
            <ListItemIcon>
              <PhoneIphoneIcon sx={{ color: "#e23744" }} />
            </ListItemIcon>
            <ListItemText primary="Update Mobile" sx={{ fontWeight: 600 }} />
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <EmailIcon sx={{ color: "#e23744" }} />
            </ListItemIcon>
            <ListItemText primary="Update Email" />
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <VpnKeyIcon sx={{ color: "#e23744" }} />
            </ListItemIcon>
            <ListItemText primary="Update Password" />
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <LogoutIcon sx={{ color: "#e23744" }} />
            </ListItemIcon>
            <ListItemText primary="Log Out" />
          </ListItem>
        </List>
      </Drawer>

      {/* Main content */}
      <Box
        sx={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          width: "100vw",
        }}
      >
        <Box
          sx={{
            width: "100vw",
            maxWidth: 1200,
            px: 2,
            py: 3,
            display: "flex",
            flexDirection: "column",
            gap: 3,
            flexGrow: 1,
          }}
        >
          {/* Metrics Row */}
          <Box sx={{
            width: "100%",
            display: "flex",
            flexDirection: "row",
            gap: 3,
            justifyContent: "stretch",
          }}>
            <Card sx={{
              flex: 1, minWidth: 0, p: 3, borderRadius: 2, boxShadow: 2
            }}>
              <Box display="flex" alignItems="center" mb={1}>
                <StarIcon sx={{ color: "#e23744", fontSize: 32, mr: 1.3 }} />
                <Typography fontWeight={700} sx={{ color: "#e23744", fontSize: 17 }}>
                  Available Loyalty
                </Typography>
              </Box>
              <Typography variant="h5" fontWeight={900}>₹0</Typography>
              <Typography variant="body2" sx={{ opacity: .7 }}>
                Blocked (In Cart, Payment Initiated)
              </Typography>
            </Card>
            <Card sx={{
              flex: 1, minWidth: 0, p: 3, borderRadius: 2, boxShadow: 2
            }}>
              <Box display="flex" alignItems="center" mb={1}>
                <WalletIcon sx={{ color: "#e23744", fontSize: 32, mr: 1.3 }} />
                <Typography fontWeight={700} sx={{ color: "#e23744", fontSize: 17 }}>
                  My Wallet Limit
                </Typography>
              </Box>
              <Typography variant="h5" fontWeight={900}>₹0</Typography>
              <Button size="small" variant="contained"
                sx={{
                  bgcolor: "#e23744", color: "#fff",
                  borderRadius: 99, px: 2.5, fontWeight: 700, fontSize: 13,
                  mt: 1,
                  width: "fit-content"
                }}>
                STATUS ACTIVE
              </Button>
            </Card>
            <Card sx={{
              flex: 1, minWidth: 0, p: 3, borderRadius: 2, boxShadow: 2
            }}>
              <Box display="flex" alignItems="center" mb={1}>
                <ShoppingCartIcon sx={{ color: "#e23744", fontSize: 32, mr: 1.3 }} />
                <Typography fontWeight={700} sx={{ color: "#e23744", fontSize: 17 }}>
                  Cart Status
                </Typography>
              </Box>
              <Typography variant="h5" fontWeight={900}>Active</Typography>
            </Card>
          </Box>
          {/* Orders and Assets Row */}
          <Box sx={{
            width: "100%",
            display: "flex",
            flexDirection: "row",
            gap: 3,
            justifyContent: "stretch",
          }}>
            <Card sx={{
              flex: 2, minWidth: 0, borderRadius: 2, boxShadow: 2, p: 0
            }}>
              <Box display="flex" alignItems="center" justifyContent="space-between" px={3} pt={2}>
                <Typography variant="h6" fontWeight={900} sx={{ color: "#e23744" }}>
                  My Last 5 Orders
                </Typography>
                <Button variant="text"
                  sx={{
                    background: "#ffe4ec", color: "#e23744",
                    borderRadius: 99, fontWeight: 700,
                    textTransform: "none", px: 3, py: .7,
                  }}>
                  More
                </Button>
              </Box>
              <Divider sx={{ mt: 2, mb: 2 }} />
              <Typography sx={{ opacity: 0.7, px: 3, pb:2 }}>No records found</Typography>
            </Card>
            <Card sx={{
              flex: 1, minWidth: 0, borderRadius: 2, boxShadow: 2, p: 0
            }}>
              <Box display="flex" alignItems="center" justifyContent="space-between" px={3} pt={2}>
                <Typography variant="h6" fontWeight={900} sx={{ color: "#e23744" }}>
                  My Assets
                </Typography>
                <Button variant="text"
                  sx={{
                    background: "#ffe4ec", color: "#e23744",
                    borderRadius: 99, fontWeight: 700,
                    textTransform: "none", px: 3, py: .7,
                  }}>
                  More
                </Button>
              </Box>
              <Divider sx={{ mt: 2, mb: 2 }} />
              <Typography sx={{ opacity: 0.7, px: 3, pb:2 }}>No records found</Typography>
            </Card>
          </Box>
        </Box>
      </Box>
      {/* FOOTER */}
      <Box
        sx={{
          bgcolor: "#fff",
          color: "#e23744",
          py: 2,
          textAlign: "center",
          fontWeight: 600,
          letterSpacing: ".3px",
          fontSize: 15,
          width: "100vw",
          borderTop: "1px solid #f3dadd"
        }}
      >
        &copy; 2025 FUELnow. All rights reserved. | Contact | Privacy Policy
      </Box>
    </Box>
  );
}
