import React, { useState } from 'react';
import {
  Box, Card, CardContent, Typography, TextField, Button, Divider
} from '@mui/material';
import { GoogleLogin } from '@react-oauth/google';

// Accept a 'role' prop: 'customer' (default), 'merchant', 'aggregator'
function SignupPage({ role = 'customer' }) {
  const [showOTP, setShowOTP] = useState(false);
  const [phone, setPhone] = useState('');
  const [name, setName] = useState('');
  const [otp, setOTP] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  // New fields for merchants and aggregators
  const [vendorCode, setVendorCode] = useState('');
  const [payerCode, setPayerCode] = useState('');
  const [pan, setPAN] = useState('');
  const [gst, setGST] = useState('');

  // Handles the OTP Request
  const requestOTP = async () => {
    setLoading(true);
    setMessage('');

    if (!name.trim()) {
      setMessage('Please enter your full name');
      setLoading(false);
      return;
    }
    if (!/^\d{10}$/.test(phone)) {
      setMessage('Enter a valid 10-digit phone number');
      setLoading(false);
      return;
    }
    // Additional checks for merchant/aggregator fields
    if (role === 'merchant' && (!vendorCode.trim() || !pan.trim() || !gst.trim())) {
      setMessage('Please enter Vendor code, PAN, and GST');
      setLoading(false);
      return;
    }
    if (role === 'aggregator' && (!payerCode.trim() || !pan.trim() || !gst.trim())) {
      setMessage('Please enter Payer code, PAN, and GST');
      setLoading(false);
      return;
    }

    try {
      const res = await fetch('/api/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        // Send additional fields for relevant roles
        body: JSON.stringify({
          phone,
          name,
          ...(role === 'merchant' && { vendorCode, pan, gst }),
          ...(role === 'aggregator' && { payerCode, pan, gst }),
          role
        })
      });
      const data = await res.json();
      if (data.success) {
        setShowOTP(true);
        setMessage('OTP sent! Please check your mobile.');
      } else {
        setMessage(data.message || 'Failed to send OTP');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  // Handles OTP verification
  const handleSignUp = async () => {
    setLoading(true);
    setMessage('');

    if (!/^\d{4,6}$/.test(otp)) {
      setMessage('Please enter a valid OTP.');
      setLoading(false);
      return;
    }

    try {
      const res = await fetch('/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, otp })
      });
      const data = await res.json();
      if (data.success) {
        setMessage('Sign Up successful!');
        // Add redirect or additional logic here if needed
      } else {
        setMessage(data.message || 'OTP verification failed');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  // Render extra fields based on role
  const renderRoleFields = () => {
    if (role === 'merchant') {
      return (
        <>
          <TextField
            label="Vendor Code"
            variant="outlined"
            fullWidth
            sx={{ mb: 2 }}
            value={vendorCode}
            onChange={(e) => setVendorCode(e.target.value)}
          />
          <TextField
            label="PAN Number"
            variant="outlined"
            placeholder="e.g AAAPL1234C"
            fullWidth
            sx={{ mb: 2 }}
            value={pan}
            onChange={(e) => setPAN(e.target.value)}
          />
          <TextField
            label="GST Number"
            variant="outlined"
            fullWidth
            sx={{ mb: 2 }}
            value={gst}
            onChange={(e) => setGST(e.target.value)}
          />
        </>
      );
    }
    if (role === 'aggregator') {
      return (
        <>
          <TextField
            label="Payer Code"
            variant="outlined"
            fullWidth
            sx={{ mb: 2 }}
            value={payerCode}
            onChange={(e) => setPayerCode(e.target.value)}
          />
          <TextField
            label="PAN Number"
            variant="outlined"
            placeholder="e.g AAAPL1234C"
            fullWidth
            sx={{ mb: 2 }}
            value={pan}
            onChange={(e) => setPAN(e.target.value)}
          />
          <TextField
            label="GST Number"
            variant="outlined"
            fullWidth
            sx={{ mb: 2 }}
            value={gst}
            onChange={(e) => setGST(e.target.value)}
          />
        </>
      );
    }
    return null;
  };

  return (
    <Box sx={{
      minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', bgcolor: '#fff'
    }}>
      <Card sx={{
        minWidth: 350, maxWidth: 380, borderRadius: 4, boxShadow: 6
      }}>
        <CardContent>
          <Typography variant="h5" align="center" sx={{ mt: 2, color: '#e23744', fontWeight: 700 }}>
            Sign Up
          </Typography>
          <Box sx={{ mt: 3 }}>
            {!showOTP ? (
              <>
                <TextField
                  label="Full Name"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
                <TextField
                  label="Phone Number"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={phone}
                  onChange={(e) => setPhone(e.target.value.replace(/[^0-9]/g, '').slice(0, 10))}
                  inputProps={{ maxLength: 10 }}
                />
                {renderRoleFields()}
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }} disabled={loading} onClick={requestOTP}>
                  {loading ? "Requesting..." : "Request OTP"}
                </Button>
              </>
            ) : (
              <>
                <TextField
                  label="Enter OTP"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={otp}
                  onChange={(e) => setOTP(e.target.value.replace(/[^0-9]/g, '').slice(0, 6))}
                  inputProps={{ maxLength: 6 }}
                />
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }} disabled={loading} onClick={handleSignUp}>
                  {loading ? "Verifying..." : "Sign Up"}
                </Button>
              </>
            )}
            {message && <Typography color="error" sx={{ mt: 2 }}>{message}</Typography>}
          </Box>
          <Divider sx={{ my: 3 }}>OR</Divider>
          <GoogleLogin
            onSuccess={credentialResponse => {
              alert('Google Auth Success!');
            }}
            onError={() => {
              alert('Google Auth Failed');
            }}
            width="100%"
            useOneTap
          />
          <Typography variant="body2" align="center" sx={{ mt: 2 }}>
            Already have an account? <a href="/login" style={{ color: '#e23744', textDecoration: 'none' }}>Login</a>
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
}

export default SignupPage;
