import React, { useState } from 'react';
import {
  Box, Card, CardContent, Typography, TextField, Button, Divider
} from '@mui/material';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

function MerchantDetailsForm({ onSubmit, loading }) {
  const [payerCode, setPayerCode] = useState('');
  const [pan, setPan] = useState('');
  const [gst, setGst] = useState('');
  const [error, setError] = useState('');

  const validate = () => {
    if (!payerCode.trim() || !pan.trim() || !gst.trim())
      return setError('All fields are required.');
    if (!/^([A-Z]{5}[0-9]{4}[A-Z])$/.test(pan.trim().toUpperCase()))
      return setError('Invalid PAN format.');
    if (!/^([0-9]{2}[A-Z]{5}[0-9]{4}[A-Z]{1}[1-9A-Z]{1}[Z]{1}[0-9A-Z]{1})$/.test(gst.trim().toUpperCase()))
      return setError('Invalid GST format.');
    setError('');
    onSubmit({ payerCode, pan, gst });
  };

  return (
    <Box display="flex" justifyContent="center">
      <Card sx={{ display: 'flex', minWidth: 900, boxShadow: 6 }}>
        {/* Left: Green visual panel */}
        <Box sx={{
          width: 310, minHeight: 400, bgcolor: 'linear-gradient(135deg,#f8fcfe 60%,#bdd88c 100%)',
          background: "linear-gradient(135deg,#23a93c 65%,#e6ffb1 100%)",
          display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'start', p: 4
        }}>
          <Typography variant="h5" sx={{ color: '#fff', fontWeight: 800, mb: 1 }}>
            <span style={{ fontWeight: 400 }}>WELCOME TO</span><br />
            Fuel4U
          </Typography>
          <Typography sx={{ color: "#f8fff8", fontSize: 20, fontWeight: 700 }}>Door Delivery of Fuel</Typography>
          <Typography sx={{ mt: 3, color: "#e1f8d5", fontWeight: 400, fontSize: 16 }}>
            If you already have an account
          </Typography>
          <Button
            variant="contained"
            sx={{ background: "#309e35", mt: 2, fontWeight: 700, px: 3 }}
            onClick={() => window.location.href = "/login"}
          >LOGIN</Button>
        </Box>

        {/* Right: Merchant KYC form */}
        <Box sx={{
          flex: 1, p: 5, display: 'flex', flexDirection: 'column', justifyContent: 'center', minWidth: 350, bgcolor: "#fff"
        }}>
          <Typography variant="h4" sx={{ fontWeight: 700, mb: 2, color: "#23a93c" }}>
            Verify your account
          </Typography>
          <Typography sx={{ mb: 4, color: "#111" }}>
            Please enter correct combination of below details.
          </Typography>
          <TextField
            required
            label="Payer code"
            fullWidth
            sx={{ mb: 2 }}
            value={payerCode}
            onChange={e => setPayerCode(e.target.value)}
          />
          <TextField
            required
            label="PAN number"
            fullWidth
            sx={{ mb: 2 }}
            value={pan}
            onChange={e => setPan(e.target.value.toUpperCase())}
            placeholder="e.g AAAPL1234C"
            inputProps={{ maxLength: 10 }}
          />
          <TextField
            required
            label="GST number"
            fullWidth
            sx={{ mb: 3 }}
            value={gst}
            onChange={e => setGst(e.target.value.toUpperCase())}
            placeholder="e.g 12AAAPL1234C1Z1"
            inputProps={{ maxLength: 15 }}
          />
          {error && <Typography color="error" sx={{ mb: 2 }}>{error}</Typography>}
          <Button
            variant="contained"
            fullWidth
            sx={{ background: "#23a93c", fontWeight: 700, fontSize: 18, py: 1.2 }}
            disabled={loading}
            onClick={validate}
          >
            {loading ? "SUBMITTING..." : "SUBMIT"}
          </Button>
        </Box>
      </Card>
    </Box>
  );
}

function SignupPage({ role }) {
  const [showOTP, setShowOTP] = useState(false);
  const [phone, setPhone] = useState('');
  const [name, setName] = useState('');
  const [otp, setOTP] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [kycMode, setKycMode] = useState(false); // For merchant: show KYC form after OTP

  const navigate = useNavigate();

  // Standard OTP request logic
  const requestOTP = async () => {
    setLoading(true);
    setMessage('');
    if (!name.trim()) {
      setMessage('Please enter your full name');
      setLoading(false);
      return;
    }
    if (!/^\d{10}$/.test(phone)) {
      setMessage('Enter a valid 10-digit phone number');
      setLoading(false);
      return;
    }
    try {
      const res = await fetch('/api/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, name })
      });
      const data = await res.json();
      if (data.success) {
        setShowOTP(true);
        setMessage('OTP sent! Please check your mobile.');
      } else {
        setMessage(data.message || 'Failed to send OTP');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  // For merchants, do KYC after OTP
  const handleOtpSubmitMerchant = async () => {
    setLoading(true);
    setMessage('');
    if (!/^\d{4,6}$/.test(otp)) {
      setMessage('Please enter a valid OTP.');
      setLoading(false);
      return;
    }
    try {
      const res = await fetch('/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, otp })
      });
      const data = await res.json();
      if (data.success) {
        setMessage('OTP verified! Please complete KYC.');
        setKycMode(true);
      } else {
        setMessage(data.message || 'OTP verification failed');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  // For all other users: normal OTP login flow
  const handleSignupRegular = async () => {
    setLoading(true);
    setMessage('');
    if (!/^\d{4,6}$/.test(otp)) {
      setMessage('Please enter a valid OTP.');
      setLoading(false);
      return;
    }
    try {
      const res = await fetch('/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, otp })
      });
      const data = await res.json();
      if (data.success) {
        localStorage.setItem('isLoggedIn', 'true');
        setMessage('Sign Up successful!');
        navigate("/dashboard");
      } else {
        setMessage(data.message || 'OTP verification failed');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  // Merchant: called after successful KYC form
  const finishMerchantKyc = async (details) => {
    setLoading(true);
    try {
      // TODO: integrate with your backend
      // Example submission: send KYC info + phone/name as needed
      const res = await fetch('/api/merchant-kyc', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, phone, ...details })
      });
      const data = await res.json();
      if (data.success) {
        localStorage.setItem('isLoggedIn', 'true');
        navigate("/dashboard");
      } else {
        // Optionally, show message if backend returns error
        setMessage(data.message || "KYC submission failed");
      }
    } catch {
      setMessage('Network or server error during KYC.');
    }
    setLoading(false);
  };

  // Google login handler (all user roles)
  const handleGoogleLoginSuccess = (credentialResponse) => {
    try {
      localStorage.setItem('isLoggedIn', 'true');
      navigate("/dashboard");
    } catch (e) {
      alert('Google Auth processing error!');
    }
  };

  // For merchants, handle all KYC stages and override display
  if (role === "merchant" && kycMode) {
    return (
      <MerchantDetailsForm
        loading={loading}
        onSubmit={finishMerchantKyc}
      />
    );
  }

  // For all other user roles
  return (
    <Box sx={{
      minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', bgcolor: '#fff'
    }}>
      <Card sx={{
        minWidth: 350, maxWidth: 380, borderRadius: 4, boxShadow: 6
      }}>
        <CardContent>
          <Typography variant="h5" align="center" sx={{ mt: 2, color: '#e23744', fontWeight: 700 }}>
            Sign Up
          </Typography>
          <Box sx={{ mt: 3 }}>
            {!showOTP ? (
              <>
                <TextField
                  label="Full Name"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
                <TextField
                  label="Phone Number"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={phone}
                  onChange={(e) => setPhone(e.target.value.replace(/[^0-9]/g, ''))}
                  inputProps={{ maxLength: 10 }}
                />
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }} disabled={loading} onClick={requestOTP}>
                  {loading ? "Requesting..." : "Request OTP"}
                </Button>
              </>
            ) : (
              <>
                <TextField
                  label="Enter OTP"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={otp}
                  onChange={(e) => setOTP(e.target.value.replace(/[^0-9]/g, ''))}
                  inputProps={{ maxLength: 6 }}
                />
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }}
                  disabled={loading}
                  onClick={role === "merchant" ? handleOtpSubmitMerchant : handleSignupRegular}
                >
                  {loading ? "Verifying..." : "Sign Up"}
                </Button>
              </>
            )}
            {message && <Typography color="error" sx={{ mt: 2 }}>{message}</Typography>}
          </Box>
          <Divider sx={{ my: 3 }}>OR</Divider>
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onError={() => {
              alert('Google Auth Failed');
            }}
            width="100%"
            useOneTap
          />
          <Typography variant="body2" align="center" sx={{ mt: 2 }}>
            Already have an account? <a href="/login" style={{ color: '#e23744', textDecoration: 'none' }}>Login</a>
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
}

export default SignupPage;
