import React from "react";
import {
  AppBar, Toolbar, Typography, IconButton, Avatar, Box, Card, Divider, Button, Badge, Drawer, List, ListItem, ListItemIcon, ListItemText, Menu, MenuItem, Grid
} from "@mui/material";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import WalletIcon from "@mui/icons-material/AccountBalanceWallet";
import StarIcon from "@mui/icons-material/Star";
import PhoneIphoneIcon from "@mui/icons-material/PhoneIphone";
import EmailIcon from "@mui/icons-material/Email";
import VpnKeyIcon from "@mui/icons-material/VpnKey";
import LogoutIcon from "@mui/icons-material/Logout";
import EditIcon from "@mui/icons-material/Edit";
import PersonIcon from "@mui/icons-material/Person";

const navLinks = [
  { label: "Home", dropdown: [] },
  { label: "Masters", dropdown: ["Vehicle Master", "Product Master"] },
  { label: "Order", dropdown: ["Place an Order", "My Cart", "My Orders"] },
  { label: "Reports", dropdown: ["Order History", "Wallet Reports"] },
  { label: "Integration Services", dropdown: [] },
  { label: "Customer Services", dropdown: [] },
  { label: "Learning", dropdown: [] },
];

const drawerWidth = 340;

export default function FuelNowDashboard() {
  const [profileOpen, setProfileOpen] = React.useState(false);
  const [navMenu, setNavMenu] = React.useState({ anchorEl: null, index: null });

  // Disable scrolling if profile drawer is open
  React.useEffect(() => {
    if (profileOpen) {
      document.body.style.overflow = "hidden";
      document.documentElement.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
      document.documentElement.style.overflow = "";
    }
    return () => {
      document.body.style.overflow = "";
      document.documentElement.style.overflow = "";
    };
  }, [profileOpen]);

  const handleNavOpen = (event, idx) => {
    setNavMenu({ anchorEl: event.currentTarget, index: idx });
  };

  const handleNavClose = () => {
    setNavMenu({ anchorEl: null, index: null });
  };

  return (
    <Box sx={{ minHeight: "100vh", bgcolor: "#f9f9f9", width: "100vw", display: "flex", flexDirection: "column" }}>
      {/* NAVBAR */}
      <AppBar position="static" sx={{ bgcolor: "#fff", color: "#222", boxShadow: 2, width: "100vw" }}>
        <Toolbar sx={{
          minHeight: 75,
          width: "100vw",
          px: 3,
          justifyContent: "flex-start",
          gap: 3
        }}>
          {/* Branding */}
          <Box sx={{ display: "flex", alignItems: "center" }}>
            {/* Logo Placeholder */}
            <Box sx={{
              width: 47, height: 47, bgcolor: "#e23744", borderRadius: "50%",
              display: "flex", alignItems: "center", justifyContent: "center", mr: 1.5
            }}>
              <StarIcon sx={{ fontSize: 32, color: "#fff" }} />
            </Box>
            <Typography variant="h5" fontWeight={900} sx={{
              fontSize: 29, letterSpacing: 2, color: "#e23744"
            }}>FUELnow</Typography>
          </Box>
          {/* Nav Links */}
          <Box sx={{ flex: 1, ml: 6, display: "flex", gap: 2, alignItems: "center" }}>
            {navLinks.map((item, idx) => (
              <Box key={item.label}>
                <Button
                  onMouseEnter={item.dropdown.length ? (e) => handleNavOpen(e, idx) : undefined}
                  onMouseLeave={handleNavClose}
                  aria-owns={navMenu.index === idx ? "nav-menu" : undefined}
                  sx={{
                    color: "#222", fontWeight: 700, px: 2, fontSize: 16, borderRadius: 2,
                    textTransform: "none", bgcolor: navMenu.index === idx ? "#ffe868" : "transparent",
                    "&:hover": { bgcolor: "#ffe868" }
                  }}
                >
                  {item.label}
                </Button>
                {/* Dropdown Menu */}
                {item.dropdown.length > 0 && navMenu.index === idx && (
                  <Menu
                    id="nav-menu"
                    anchorEl={navMenu.anchorEl}
                    open={Boolean(navMenu.anchorEl) && navMenu.index === idx}
                    onClose={handleNavClose}
                    MenuListProps={{
                      onMouseLeave: handleNavClose,
                      onMouseEnter: (e) => e.stopPropagation()
                    }}
                    sx={{ mt: 1 }}
                  >
                    {item.dropdown.map(opt =>
                      <MenuItem key={opt} onClick={handleNavClose} sx={{ fontWeight: 600 }}>
                        {opt}
                      </MenuItem>
                    )}
                  </Menu>
                )}
              </Box>
            ))}
          </Box>
          {/* Cart & Profile */}
          <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
            <IconButton size="large" sx={{ color: "#e23744" }}>
              <Badge badgeContent={0} color="error">
                <ShoppingCartIcon sx={{ fontSize: 27 }} />
              </Badge>
            </IconButton>
            <Typography
              sx={{
                fontWeight: 600, mr: 2, color: "#222", fontSize: 17,
                display: { xs: "none", sm: "inline-block" }
              }}
            >Hello, Jai Devarajan</Typography>
            <IconButton onClick={() => setProfileOpen(true)} sx={{ p: 0 }}>
              <Avatar sx={{
                width: 41, height: 41,
                bgcolor: "#eafce5", color: "#43b91a",
                fontWeight: 700, fontSize: 20
              }}>
                <PersonIcon />
              </Avatar>
            </IconButton>
          </Box>
        </Toolbar>
        {/* Second level: Yellow bar */}
        <Box sx={{ bgcolor: "#ffe100", width: "100vw", minHeight: 8 }} />
      </AppBar>

      {/* Profile Drawer */}
      <Drawer
        anchor="right"
        open={profileOpen}
        onClose={() => setProfileOpen(false)}
        sx={{
          "& .MuiDrawer-paper": {
            width: drawerWidth,
            borderTopLeftRadius: 27,
            borderBottomLeftRadius: 27,
            boxShadow: 7,
            bgcolor: "#fff"
          }
        }}
      >
        <Box sx={{ p: 4, pt: 5, display: "flex", flexDirection: "column", alignItems: "center" }}>
          <IconButton
            sx={{ position: "absolute", right: 16, top: 14 }}
            onClick={() => setProfileOpen(false)}
            aria-label="Close"
          >
            <Typography variant="h5" sx={{ fontWeight: 700, color: "#222" }}>×</Typography>
          </IconButton>
          <Avatar sx={{
            width: 96, height: 96, bgcolor: "#eafce5", color: "#43b91a",
            fontWeight: 800, fontSize: 52, mb: 2, boxShadow: 2
          }}>
            <PersonIcon sx={{ fontSize: 60 }} />
          </Avatar>
          <Typography fontWeight={700} sx={{ fontSize: 24, color: "#222", mb: 2 }}>
            jai devarajan
          </Typography>
          <Button
            startIcon={<EditIcon />}
            variant="contained"
            sx={{
              borderRadius: 99, bgcolor: "#43b91a", color: "#fff",
              fontWeight: 700, px: 5, boxShadow: 2, fontSize: 18,
              mb: 3, textTransform: "none", '&:hover': { bgcolor: "#369c17" }
            }}
          >Edit Profile</Button>
        </Box>
        <Divider />
        <List>
          <ListItem button>
            <ListItemIcon>
              <PhoneIphoneIcon sx={{ color: "#43b91a" }} />
            </ListItemIcon>
            <ListItemText primary="Update Mobile" sx={{ fontWeight: 600 }} />
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <EmailIcon sx={{ color: "#43b91a" }} />
            </ListItemIcon>
            <ListItemText primary="Update Email" />
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <VpnKeyIcon sx={{ color: "#43b91a" }} />
            </ListItemIcon>
            <ListItemText primary="Update Password" />
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <LogoutIcon sx={{ color: "#43b91a" }} />
            </ListItemIcon>
            <ListItemText primary="Log Out" />
          </ListItem>
        </List>
      </Drawer>

      {/* MAIN CONTENT */}
      <Box sx={{
        px: 2,
        pt: 4,
        flex: 1,
        width: "100vw",
        maxWidth: "100vw",
        display: "flex",
        flexDirection: "column",
        alignItems: "stretch"
      }}>
        {/* METRICS ROW */}
        <Grid container spacing={3}>
          <Grid item xs={12} sm={6} lg={4}>
            <Box sx={{
              bgcolor: "linear-gradient(135deg, #3ac55b 0%, #17863d 100%)",
              background: "linear-gradient(135deg, #3ac55b 0%, #17863d 100%)",
              borderRadius: 3,
              minHeight: 146,
              display: "flex", flexDirection: "column", justifyContent: "space-between", p: 2.7, boxShadow: 2
            }}>
              <Box display="flex" alignItems="center" gap={1}>
                <Button disableElevation
                  sx={{
                    background: "#185c37", color: "#fff",
                    fontWeight: 800, borderRadius: 2, minWidth: 0, px: 2, py: .3, fontSize: 15
                  }}
                >Available Loyalty</Button>
                <Typography ml="auto" fontWeight={700} color="#fff" fontSize={15}>
                  Status <span style={{ color: "#ffe100", marginLeft: 4 }}>Active</span>
                </Typography>
              </Box>
              <Typography variant="h4" mt={2} color="#fff" fontWeight={900}>₹0</Typography>
              <Typography fontWeight={500} color="#d9ffe1" sx={{ fontSize: 16 }}>
                Available Loyalty - Blocked(In Cart, Payment Initiated)
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} sm={6} lg={4}>
            <Box sx={{
              bgcolor: "linear-gradient(135deg, #ffb400 0%, #fc8a00 100%)",
              background: "linear-gradient(135deg, #ffe19c 0%, #ffc33b 100%)",
              borderRadius: 3,
              minHeight: 146,
              display: "flex", flexDirection: "column", justifyContent: "space-between", p: 2.7, boxShadow: 2
            }}>
              <Box display="flex" alignItems="center" gap={1}>
                <Button disableElevation
                  sx={{
                    background: "#ffb400", color: "#fff",
                    fontWeight: 800, borderRadius: 2, minWidth: 0, px: 2, py: .3, fontSize: 15
                  }}>My Wallet Limit</Button>
                <Typography ml="auto" fontWeight={700} color="#fff" fontSize={15}>
                  Status <span style={{ color: "#17863d", marginLeft: 4 }}>Active</span>
                </Typography>
              </Box>
              <Typography variant="h4" mt={2} color="#fff" fontWeight={900}>₹0</Typography>
              <Typography fontWeight={500} color="#fff2ce" sx={{ fontSize: 16 }}>
                Amount available in your wallet
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} lg={4}>
            <Box sx={{
              bgcolor: "#fff",
              borderRadius: 3,
              minHeight: 146,
              display: "flex", flexDirection: "column", justifyContent: "space-between", p: 2.6, boxShadow: 2
            }}>
              <Box display="flex" alignItems="center" gap={1}>
                <Typography
                  fontWeight={900}
                  sx={{ color: "#222", fontSize: 24, flex: 1, mb: 2 }}
                >My Last 5 Orders</Typography>
                <Button
                  variant="outlined"
                  sx={{
                    background: "#fff",
                    color: "#43b91a",
                    border: "1.8px solid #43b91a",
                    borderRadius: 80, fontWeight: 700, fontSize: 16, px: 3, py: .5, textTransform: "none", "&:hover": { bgcolor: "#eafce5" }
                  }}>More</Button>
              </Box>
              <Typography color="#555" fontWeight={600} sx={{ fontSize: 18, ml: .5, mb: .5 }}>No records found</Typography>
            </Box>
          </Grid>
        </Grid>
        {/* My Assets */}
        <Box
          sx={{
            mt: 4,
            background: "#fff",
            borderRadius: 3,
            px: 2.2,
            py: 3,
            boxShadow: 2,
            minHeight: 260,
            maxWidth: "100%",
          }}
        >
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
            <Typography variant="h5" fontWeight={900} color="#222">
              My Assets
            </Typography>
            <Button variant="outlined"
              sx={{
                border: "1.8px solid #43b91a", color: "#43b91a",
                borderRadius: 99, fontWeight: 700,
                textTransform: "none", px: 3, py: .4,
                background: "#eafce5", "&:hover": { bgcolor: "#d2f9d6" }
              }}>
              More
            </Button>
          </Box>
          <Divider sx={{ mb: 3 }} />
          <Typography color="#666" fontSize={18} fontWeight={600}>
            No records found
          </Typography>
        </Box>
      </Box>

      {/* FOOTER */}
      <Box
        sx={{
          bgcolor: "#f9f9f9",
          color: "#222",
          py: 2.2,
          textAlign: "center",
          fontWeight: 500,
          letterSpacing: ".3px",
          fontSize: 15,
          width: "100vw",
          borderTop: "1.2px solid #e2e2e2"
        }}
      >
        &copy; 2025 FUELnow. All rights reserved. | Home | About Us | Contact Us | Privacy Policy | Legal Notice | Cancellation and Refund Policy
      </Box>
    </Box>
  );
}
