import React, { useState } from 'react';
import {
  Box, Card, CardContent, Typography, TextField, Button, Divider
} from '@mui/material';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

function SignupPage({ role }) {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [otp, setOTP] = useState('');
  const [payerCode, setPayerCode] = useState('');
  const [pan, setPan] = useState('');
  const [gst, setGst] = useState('');
  const [showOTP, setShowOTP] = useState(false);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const requestOTP = async () => {
    setLoading(true);
    setMessage('');
    if (!name.trim()) {
      setMessage('Please enter your full name');
      setLoading(false);
      return;
    }
    if (!/^\d{10}$/.test(phone)) {
      setMessage('Enter a valid 10-digit phone number');
      setLoading(false);
      return;
    }
    // For merchant/aggregator, make sure extra fields are filled
    if ((role === "merchant" || role === "aggregator")) {
      if (!payerCode.trim() || !pan.trim() || !gst.trim()) {
        setMessage('All KYC fields are required.');
        setLoading(false);
        return;
      }
      if (!/^([A-Z]{5}[0-9]{4}[A-Z])$/.test(pan.trim().toUpperCase())) {
        setMessage('Invalid PAN format.');
        setLoading(false);
        return;
      }
      if (!/^([0-9]{2}[A-Z]{5}[0-9]{4}[A-Z]{1}[1-9A-Z]{1}[Z]{1}[0-9A-Z]{1})$/.test(gst.trim().toUpperCase())) {
        setMessage('Invalid GST format.');
        setLoading(false);
        return;
      }
    }
    try {
      const res = await fetch('/api/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, name })
      });
      const data = await res.json();
      if (data.success) {
        setShowOTP(true);
        setMessage('OTP sent! Please check your mobile.');
      } else {
        setMessage(data.message || 'Failed to send OTP');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  const handleSignup = async () => {
    setLoading(true);
    setMessage('');
    if (!/^\d{4,6}$/.test(otp)) {
      setMessage('Please enter a valid OTP.');
      setLoading(false);
      return;
    }
    // For merchant/aggregator, make sure extra fields are filled even at OTP step
    if ((role === "merchant" || role === "aggregator")) {
      if (!payerCode.trim() || !pan.trim() || !gst.trim()) {
        setMessage('All KYC fields are required.');
        setLoading(false);
        return;
      }
      if (!/^([A-Z]{5}[0-9]{4}[A-Z])$/.test(pan.trim().toUpperCase())) {
        setMessage('Invalid PAN format.');
        setLoading(false);
        return;
      }
      if (!/^([0-9]{2}[A-Z]{5}[0-9]{4}[A-Z]{1}[1-9A-Z]{1}[Z]{1}[0-9A-Z]{1})$/.test(gst.trim().toUpperCase())) {
        setMessage('Invalid GST format.');
        setLoading(false);
        return;
      }
    }
    try {
      // Send all details to backend (for merchant/aggregator)
      const payload = { name, phone, otp };
      if (role === "merchant" || role === "aggregator") {
        payload.payerCode = payerCode;
        payload.pan = pan.toUpperCase();
        payload.gst = gst.toUpperCase();
      }
      const res = await fetch((role === "merchant" || role === "aggregator")
        ? `/api/${role}-signup`
        : '/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      if (data.success) {
        localStorage.setItem('isLoggedIn', 'true');
        setMessage('Sign Up successful!');
        navigate("/dashboard");
      } else {
        setMessage(data.message || "Signup failed");
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  const handleGoogleLoginSuccess = () => {
    try {
      localStorage.setItem('isLoggedIn', 'true');
      navigate("/dashboard");
    } catch (e) {
      alert('Google Auth processing error!');
    }
  };

  return (
    <Box sx={{
      minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', bgcolor: '#fff'
    }}>
      <Card sx={{
        minWidth: 350, maxWidth: 400, borderRadius: 4, boxShadow: 6
      }}>
        <CardContent>
          <Typography variant="h5" align="center" sx={{ mt: 2, color: '#e23744', fontWeight: 700 }}>
            Sign Up
          </Typography>
          <Box sx={{ mt: 3 }}>
            <TextField
              label="Full Name"
              variant="outlined"
              fullWidth
              sx={{ mb: 2 }}
              value={name}
              onChange={(e) => setName(e.target.value)}
              disabled={showOTP}
            />
            <TextField
              label="Phone Number"
              variant="outlined"
              fullWidth
              sx={{ mb: 2 }}
              value={phone}
              onChange={(e) => setPhone(e.target.value.replace(/[^0-9]/g, ''))}
              inputProps={{ maxLength: 10 }}
              disabled={showOTP}
            />
            {(role === "merchant" || role === "aggregator") && (
              <>
                <TextField
                  label={role === "merchant" ? "Payer Code" : "Vendor Code"}
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={payerCode}
                  onChange={(e) => setPayerCode(e.target.value)}
                  disabled={showOTP}
                />
                <TextField
                  label="PAN Number"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={pan}
                  onChange={(e) => setPan(e.target.value.toUpperCase())}
                  placeholder="e.g AAAPL1234C"
                  inputProps={{ maxLength: 10 }}
                  disabled={showOTP}
                />
                <TextField
                  label="GST Number"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={gst}
                  onChange={(e) => setGst(e.target.value.toUpperCase())}
                  placeholder="e.g 12AAAPL1234C1Z1"
                  inputProps={{ maxLength: 15 }}
                  disabled={showOTP}
                />
              </>
            )}
            {showOTP &&
              <TextField
                label="Enter OTP"
                variant="outlined"
                fullWidth
                sx={{ mb: 2 }}
                value={otp}
                onChange={(e) => setOTP(e.target.value.replace(/[^0-9]/g, ''))}
                inputProps={{ maxLength: 6 }}
              />
            }
            <Button fullWidth variant="contained" sx={{
              bgcolor: '#e23744', color: '#fff', borderRadius: 99,
              fontWeight: 700, fontSize: '1rem', py: 1.2,
              '&:hover': { bgcolor: '#b81a34' }
            }}
              disabled={loading}
              onClick={showOTP ? handleSignup : requestOTP}
            >
              {showOTP ? (loading ? "Verifying..." : "Sign Up") : (loading ? "Requesting..." : "Request OTP")}
            </Button>
            {message && <Typography color="error" sx={{ mt: 2 }}>{message}</Typography>}
          </Box>
          <Divider sx={{ my: 3 }}>OR</Divider>
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onError={() => {
              alert('Google Auth Failed');
            }}
            width="100%"
            useOneTap
          />
          <Typography variant="body2" align="center" sx={{ mt: 2 }}>
            Already have an account? <a href="/login" style={{ color: '#e23744', textDecoration: 'none' }}>Login</a>
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
}

export default SignupPage;
