import React, { useState } from 'react';
import {
  Box, Card, CardContent, Typography, TextField, Button, Divider
} from '@mui/material';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

function MerchantKYCForm({ onSubmit, loading }) {
  const [payerCode, setPayerCode] = useState('');
  const [pan, setPan] = useState('');
  const [gst, setGst] = useState('');
  const [error, setError] = useState('');

  const validateAndSubmit = () => {
    if (!payerCode.trim() || !pan.trim() || !gst.trim()) {
      setError('All fields are required.');
      return;
    }
    if (!/^([A-Z]{5}[0-9]{4}[A-Z])$/.test(pan.trim().toUpperCase())) {
      setError('Invalid PAN format.');
      return;
    }
    if (!/^([0-9]{2}[A-Z]{5}[0-9]{4}[A-Z]{1}[1-9A-Z]{1}[Z]{1}[0-9A-Z]{1})$/.test(gst.trim().toUpperCase())) {
      setError('Invalid GST format.');
      return;
    }
    setError('');
    onSubmit({ payerCode, pan, gst });
  };

  return (
    <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh" bgcolor="#f8f8f8">
      <Card sx={{ display: 'flex', minWidth: 900, boxShadow: 6, borderRadius: 5 }}>
        {/* Left Fuel4U Green panel */}
        <Box sx={{
          width: 330, minHeight: 400,
          background: "linear-gradient(135deg, #139c3c 65%, #d3fac5 100%)",
          display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'start', p: 4, borderTopLeftRadius: 20, borderBottomLeftRadius: 20
        }}>
          <Typography variant="h6" sx={{ color: '#f8ffea', fontWeight: 500 }}>
            WELCOME TO
          </Typography>
          <Typography variant="h4" sx={{ color: '#fff', fontWeight: 900, letterSpacing: 1, mb: 1 }}>
            Fuel4U
          </Typography>
          <Typography sx={{ color: "#fafff1", fontWeight: 800, fontSize: 20, mb: 2 }}>Door Delivery of Fuel</Typography>
          <Typography sx={{ mt: 2, color: "#e1f8d5", fontWeight: 400, fontSize: 18 }}>
            If you already have an account
          </Typography>
          <Button
            size="large"
            variant="contained"
            sx={{ background: "#309e35", mt: 2, fontWeight: 800, px: 5, fontSize: 18 }}
            onClick={() => window.location.href = "/login"}
          >LOGIN</Button>
        </Box>

        {/* Right: KYC form */}
        <Box sx={{
          flex: 1, p: 5, display: 'flex', flexDirection: 'column', justifyContent: 'center', minWidth: 400, bgcolor: "#fff", borderTopRightRadius: 20, borderBottomRightRadius: 20
        }}>
          <Typography variant="h4" sx={{ fontWeight: 900, mb: 2, color: "#16a037" }}>
            Verify your account
          </Typography>
          <Typography sx={{ mb: 3, color: "#222" }}>
            Please enter correct combination of below details.
          </Typography>
          <TextField
            required label="Payer code" fullWidth sx={{ mb: 2 }}
            value={payerCode} onChange={e => setPayerCode(e.target.value)}
          />
          <TextField
            required label="PAN number" fullWidth sx={{ mb: 2 }}
            value={pan} onChange={e => setPan(e.target.value.toUpperCase())}
            placeholder="e.g AAAPL1234C"
            inputProps={{ maxLength: 10 }}
          />
          <TextField
            required label="GST number" fullWidth sx={{ mb: 3 }}
            value={gst} onChange={e => setGst(e.target.value.toUpperCase())}
            placeholder="e.g 12AAAPL1234C1Z1"
            inputProps={{ maxLength: 15 }}
          />
          {error && <Typography color="error" sx={{ mb: 2 }}>{error}</Typography>}
          <Button
            variant="contained"
            fullWidth
            sx={{ background: "#23a93c", fontWeight: 700, fontSize: 18, py: 1.1 }}
            disabled={loading}
            onClick={validateAndSubmit}
          >
            {loading ? "SUBMITTING..." : "SUBMIT"}
          </Button>
        </Box>
      </Card>
    </Box>
  );
}

function SignupPage({ role }) {
  const [showOTP, setShowOTP] = useState(false);
  const [phone, setPhone] = useState('');
  const [name, setName] = useState('');
  const [otp, setOTP] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [kycMode, setKycMode] = useState(false);

  const navigate = useNavigate();

  // Request OTP
  const requestOTP = async () => {
    setLoading(true);
    setMessage('');
    if (!name.trim()) {
      setMessage('Please enter your full name');
      setLoading(false);
      return;
    }
    if (!/^\d{10}$/.test(phone)) {
      setMessage('Enter a valid 10-digit phone number');
      setLoading(false);
      return;
    }
    try {
      const res = await fetch('/api/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, name })
      });
      const data = await res.json();
      if (data.success) {
        setShowOTP(true);
        setMessage('OTP sent! Please check your mobile.');
      } else {
        setMessage(data.message || 'Failed to send OTP');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  // Merchant: Submit OTP, then show KYC
  const handleOtpSubmitMerchant = async () => {
    setLoading(true);
    setMessage('');
    if (!/^\d{4,6}$/.test(otp)) {
      setMessage('Please enter a valid OTP.');
      setLoading(false);
      return;
    }
    try {
      const res = await fetch('/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, otp })
      });
      const data = await res.json();
      if (data.success) {
        setMessage('OTP verified! Please complete KYC.');
        setKycMode(true);
      } else {
        setMessage(data.message || 'OTP verification failed');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  // Regular: Complete signup with OTP
  const handleSignupRegular = async () => {
    setLoading(true);
    setMessage('');
    if (!/^\d{4,6}$/.test(otp)) {
      setMessage('Please enter a valid OTP.');
      setLoading(false);
      return;
    }
    try {
      const res = await fetch('/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, otp })
      });
      const data = await res.json();
      if (data.success) {
        localStorage.setItem('isLoggedIn', 'true');
        setMessage('Sign Up successful!');
        navigate("/dashboard");
      } else {
        setMessage(data.message || 'OTP verification failed');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  // KYC submit
  const handleKycSubmit = async (details) => {
    setLoading(true);
    try {
      // Send details to backend
      const res = await fetch('/api/merchant-kyc', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, phone, ...details })
      });
      const data = await res.json();
      if (data.success) {
        localStorage.setItem('isLoggedIn', 'true');
        navigate("/dashboard");
      } else {
        setMessage(data.message || "KYC submission failed");
      }
    } catch {
      setMessage('Network or server error during KYC.');
    }
    setLoading(false);
  };

  // Google
  const handleGoogleLoginSuccess = () => {
    try {
      localStorage.setItem('isLoggedIn', 'true');
      navigate("/dashboard");
    } catch (e) {
      alert('Google Auth processing error!');
    }
  };

  // => If merchant and in KYC mode, show Fuel4U KYC UI
  if (role === "merchant" && kycMode) {
    return (
      <MerchantKYCForm
        loading={loading}
        onSubmit={handleKycSubmit}
      />
    );
  }

  // Otherwise: signup form (for all and before merchant KYC step)
  return (
    <Box sx={{
      minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', bgcolor: '#fff'
    }}>
      <Card sx={{
        minWidth: 350, maxWidth: 380, borderRadius: 4, boxShadow: 6
      }}>
        <CardContent>
          <Typography variant="h5" align="center" sx={{ mt: 2, color: '#e23744', fontWeight: 700 }}>
            Sign Up
          </Typography>
          <Box sx={{ mt: 3 }}>
            {!showOTP ? (
              <>
                <TextField
                  label="Full Name"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
                <TextField
                  label="Phone Number"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={phone}
                  onChange={(e) => setPhone(e.target.value.replace(/[^0-9]/g, ''))}
                  inputProps={{ maxLength: 10 }}
                />
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }} disabled={loading} onClick={requestOTP}>
                  {loading ? "Requesting..." : "Request OTP"}
                </Button>
              </>
            ) : (
              <>
                <TextField
                  label="Enter OTP"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={otp}
                  onChange={(e) => setOTP(e.target.value.replace(/[^0-9]/g, ''))}
                  inputProps={{ maxLength: 6 }}
                />
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }}
                  disabled={loading}
                  onClick={role === "merchant" ? handleOtpSubmitMerchant : handleSignupRegular}
                >
                  {loading ? "Verifying..." : "Sign Up"}
                </Button>
              </>
            )}
            {message && <Typography color="error" sx={{ mt: 2 }}>{message}</Typography>}
          </Box>
          <Divider sx={{ my: 3 }}>OR</Divider>
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onError={() => {
              alert('Google Auth Failed');
            }}
            width="100%"
            useOneTap
          />
          <Typography variant="body2" align="center" sx={{ mt: 2 }}>
            Already have an account? <a href="/login" style={{ color: '#e23744', textDecoration: 'none' }}>Login</a>
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
}

export default SignupPage;
