import React from "react";
import { BrowserRouter, Routes, Route, Link, useLocation } from "react-router-dom";
import { AppBar, Toolbar, Button, Container } from "@mui/material";
import { GoogleOAuthProvider } from "@react-oauth/google";

import HomePage from "./pages/HomePage";
import OrderPage from "./pages/OrderPage";
import LoginPage from "./pages/LoginPage";
import SignupPage from "./pages/SignupPage";
import MerchantSignup from "./pages/MerchantSignup";
import AggregatorSignup from "./pages/AggregatorSignup";
import UserDashboard from "./pages/UserDashboard";

// Only inject basic white background for /dashboard, let your dashboard component handle all design
function DashboardCSSWrapper({ children }) {
  const location = useLocation();
  React.useEffect(() => {
    let styleElem = null;
    if (location.pathname === "/dashboard") {
      styleElem = document.createElement("style");
      styleElem.innerHTML = `
        body, html, #root {
          background: #f7f9fa !important;
          min-height: 100vh;
        }
      `;
      document.head.appendChild(styleElem);
    }
    return () => {
      if (styleElem) document.head.removeChild(styleElem);
    };
  }, [location]);
  return children;
}

function App() {
  return (
    <GoogleOAuthProvider clientId="565209562551-8kl75442g9j39u158hpa7j8ns3rrkbma.apps.googleusercontent.com">
      <BrowserRouter>
        <DashboardCSSWrapper>
          {/* Show AppBar only on non-dashboard pages */}
          <Routes>
            <Route
              path="/dashboard"
              element={<UserDashboard />}
            />
            <Route
              path="*"
              element={
                <>
                  <AppBar position="static" color="default" elevation={1} sx={{ mb: 0, bgcolor: "#fff" }}>
                    <Toolbar>
                      <Button component={Link} to="/" color="inherit">
                        Home
                      </Button>
                      {/* All other buttons removed as per your request */}
                    </Toolbar>
                  </AppBar>
                  <Container maxWidth="md" sx={{ mt: 4 }}>
                    <Routes>
                      <Route path="/" element={<HomePage />} />
                      <Route path="/order" element={<OrderPage />} />
                      <Route path="/login" element={<LoginPage />} />
                      <Route path="/signup" element={<SignupPage />} />
                      <Route path="/merchant-signup" element={<MerchantSignup />} />
                      <Route path="/aggregator-signup" element={<AggregatorSignup />} />
                      {/* fallback: UserDashboard is handled above */}
                    </Routes>
                  </Container>
                </>
              }
            />
          </Routes>
        </DashboardCSSWrapper>
      </BrowserRouter>
    </GoogleOAuthProvider>
  );
}

export default App;
