import React, { useState } from 'react';
import {
  Box, Card, CardContent, Typography, TextField, Button, Divider
} from '@mui/material';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

function SignupPage() {
  const [showOTP, setShowOTP] = useState(false);
  const [phone, setPhone] = useState('');
  const [name, setName] = useState('');
  const [otp, setOTP] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const requestOTP = async () => {
    setLoading(true);
    setMessage('');
    if (!name.trim()) {
      setMessage('Please enter your full name');
      setLoading(false);
      return;
    }
    if (!/^\d{10}$/.test(phone)) {
      setMessage('Enter a valid 10-digit phone number');
      setLoading(false);
      return;
    }
    try {
      const res = await fetch('/api/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, name })
      });
      const data = await res.json();
      if (data.success) {
        setShowOTP(true);
        setMessage('OTP sent! Please check your mobile.');
      } else {
        setMessage(data.message || 'Failed to send OTP');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  const handleSignUp = async () => {
    setLoading(true);
    setMessage('');
    if (!/^\d{4,6}$/.test(otp)) {
      setMessage('Please enter a valid OTP.');
      setLoading(false);
      return;
    }
    try {
      const res = await fetch('/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, otp })
      });
      const data = await res.json();
      if (data.success) {
        localStorage.setItem('isLoggedIn', 'true'); // set for protected route
        setMessage('Sign Up successful!');
        // FIX: use standard push, NOT replace!
        navigate("/dashboard");
      } else {
        setMessage(data.message || 'OTP verification failed');
      }
    } catch (err) {
      setMessage('Network error, try again!');
    }
    setLoading(false);
  };

  const handleGoogleLoginSuccess = (credentialResponse) => {
    try {
      localStorage.setItem('isLoggedIn', 'true');
      // FIX: use push, not replace
      navigate("/dashboard");
    } catch (e) {
      alert('Google Auth processing error!');
    }
  };

  return (
    <Box sx={{
      minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', bgcolor: '#fff'
    }}>
      <Card sx={{
        minWidth: 350, maxWidth: 380, borderRadius: 4, boxShadow: 6
      }}>
        <CardContent>
          <Typography variant="h5" align="center" sx={{ mt: 2, color: '#e23744', fontWeight: 700 }}>
            Sign Up
          </Typography>
          <Box sx={{ mt: 3 }}>
            {!showOTP ? (
              <>
                <TextField
                  label="Full Name"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
                <TextField
                  label="Phone Number"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={phone}
                  onChange={(e) => setPhone(e.target.value.replace(/[^0-9]/g, ''))}
                  inputProps={{ maxLength: 10 }}
                />
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }} disabled={loading} onClick={requestOTP}>
                  {loading ? "Requesting..." : "Request OTP"}
                </Button>
              </>
            ) : (
              <>
                <TextField
                  label="Enter OTP"
                  variant="outlined"
                  fullWidth
                  sx={{ mb: 2 }}
                  value={otp}
                  onChange={(e) => setOTP(e.target.value.replace(/[^0-9]/g, ''))}
                  inputProps={{ maxLength: 6 }}
                />
                <Button fullWidth variant="contained" sx={{
                  bgcolor: '#e23744', color: '#fff', borderRadius: 99,
                  fontWeight: 700, fontSize: '1rem', py: 1.2,
                  '&:hover': { bgcolor: '#b81a34' }
                }} disabled={loading} onClick={handleSignUp}>
                  {loading ? "Verifying..." : "Sign Up"}
                </Button>
              </>
            )}
            {message && <Typography color="error" sx={{ mt: 2 }}>{message}</Typography>}
          </Box>
          <Divider sx={{ my: 3 }}>OR</Divider>
          <GoogleLogin
            onSuccess={handleGoogleLoginSuccess}
            onError={() => {
              alert('Google Auth Failed');
            }}
            width="100%"
            useOneTap
          />
          <Typography variant="body2" align="center" sx={{ mt: 2 }}>
            Already have an account? <a href="/login" style={{ color: '#e23744', textDecoration: 'none' }}>Login</a>
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
}

export default SignupPage;
