import React from "react";
import { Dialog, DialogTitle, DialogContent, DialogActions, Box, TextField, Button, MenuItem, InputAdornment } from "@mui/material";
import LocalGasStationOutlinedIcon from '@mui/icons-material/LocalGasStationOutlined';
import LocationOnOutlinedIcon from '@mui/icons-material/LocationOnOutlined';

const fuelTypes = [
  { value: 'petrol', label: 'Petrol' },
  { value: 'diesel', label: 'Diesel' }
];

export default function OrderForm({ open, onClose }) {
  return (
    <Dialog open={open} onClose={onClose} maxWidth="xs" fullWidth>
      <DialogTitle sx={{ fontWeight: 700, color: "#e23744", textAlign: "center" }}>Place Your Order</DialogTitle>
      <DialogContent sx={{ pb: 1 }}>
        <Box
          component="form"
          sx={{ display: "flex", flexDirection: "column", gap: 2, mt: 1 }}
        >
          <TextField
            select
            required
            label="Fuel Type"
            defaultValue=""
            fullWidth
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <LocalGasStationOutlinedIcon color="action" />
                </InputAdornment>
              )
            }}
          >
            {fuelTypes.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>
          <TextField
            required
            label="Quantity"
            type="number"
            fullWidth
            placeholder="e.g. 10"
            InputProps={{
              endAdornment: <InputAdornment position="end">Ltr</InputAdornment>
            }}
          />
          <TextField
            required
            label="Delivery Address"
            placeholder="Enter delivery address"
            fullWidth
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <LocationOnOutlinedIcon color="action" />
                </InputAdornment>
              )
            }}
          />
        </Box>
      </DialogContent>
      <DialogActions sx={{ px: 3, pb: 2, gap: 1 }}>
        <Button
          variant="contained"
          color="primary"
          sx={{
            background: "#e23744", fontWeight: 700, borderRadius: 3, px: 3, boxShadow: "none"
          }}
          fullWidth
        >
          Submit Order
        </Button>
        <Button
          variant="outlined"
          onClick={onClose}
          sx={{
            borderColor: "#e23744",
            color: "#e23744",
            borderRadius: 3,
            px: 3,
            fontWeight: 600
          }}
          fullWidth
        >
          Cancel
        </Button>
      </DialogActions>
    </Dialog>
  );
}
